	private static boolean compare(double [] expectation, double [] result, double epsilon){
		for(int i=0; i<expectation.length; i++){
			double current = Math.abs(expectation[i] - result[i]);
			if(current > epsilon){
				return false;
			}
		}
		return true;
	}
